
def move_elevator(commands):

    # We could also just add up the number of parens, but this works too.
    floor = 0
    for command in commands:
        if command == "(":
            floor = floor + 1
        elif command == ")":
            floor = floor - 1
        else:
            error = "There is no |{}| button on the elevator".format(command)
            raise ValueError(error)
    return floor

